import React,{useContext,useState} from 'react';
import {useDrag} from 'react-dnd';
import './Tile_container.css'
import {DndProvider,useDrop} from 'react-dnd'
import {HTML5Backend} from 'react-dnd-html5-backend'
import {UserContext} from '../../UserContext.js'
import {GamesContext} from '../GamesContext.js'
import {Tile} from '../Sachet.js'

const positions=[[2,1,"row","horizontal"],[1,2,"column","vertical"],[2,3,"row","horizontal"],[3,2,"column","vertical"]];

function Tile_container(value) {
  
    const {profile,setprofile}=useContext(UserContext)
    const {gameState,setgameState}=useContext(GamesContext)
    const {playerlistState,setplayerlistState}=useContext(PlayerListContext)

    const container_name="tile_container "+positions[value.player_num][3]
    const containerstyle={
      gridColumn:positions[value.player_num][0],
      gridRow:positions[value.player_num][1],
    }
    const holderstyle={
      display:"flex",
      flexDirection:positions[value.player_num][2]
    }
    useEffect(()=>{
      let active_player_num=gameState.turn_num%gameState.player_num
      let your_index=gameState.Player_list.findIndex((Player)=>Player.username==profile.username)
      let your_index_challenge = (your_index==gameState.Player_list.length-1) ? 0 : your_index+1
      let temp_playerlist=[...playerlist]
      if(active_player_num==your_index){
        temp_playerlist[your_index].delete_active=true
        setplayerlistState(temp_playerlist)
      }
      if(active_player_num!=your_index_challenge){  
        temp_playerlist[your_index].challenge_active=true
        setplayerlistState(temp_playerlist)
      }else{
        temp_playerlist[your_index].challenge_active=false
        setplayerlistState(temp_playerlist)
      }
    },[gameState.turn_num])
    /*
    const moveTileHandler=(dragIndex,hoverIndex)=>{
      const dragItem=gameState.Player_list[value.player_num].tiles[dragIndex];
      if (dragItem){
        setgameState((prevgameState=>{
          const currentgameState={...prevgameState}
          const prevItem=currentgameState.splice(hoverIndex,1,dragItem)
          currentgameState.splice(dragIndex,1,prevItem[0])
          return currentgameState
        }))
      }
    }
    */

    
    return (
      
      <div className={container_name} style={containerstyle}>
          <div className="name_display">{gameState.Player_list[value.player_num].username}</div>   
          <div className="score_display">{gameState.Player_list[value.player_num].score}</div> 
          <div className="tile_holder" style={holderstyle}>

              
                {gameState.Player_list[value.player_num].tiles.map((tile,pos)=>(
                  <Tile tile={tile} key={pos}/>
                ))}  
              
          </div>
          <button type="button" className="challengebtn" visibility={gameState.Player_list[value.player_num].challenge_active?"visible":"hidden"}>Challenge</button> 
          <img src=".../UI_component/delete.png" className="delete_img" visibility={gameState.Player_list[value.player_num].delete_active?"visible":"hidden"} />                
      </div>
    );
    
  }
    
export default Tile_container;
