import React, {useState} from 'react';
import "./Signup.css"
import Popup from 'reactjs-popup';
import { useFormik } from 'formik';
import * as Yup from 'yup';
 

function Signup(){

  const formik = useFormik({
    initialValues: {
      username: '',
      email: '',
      password:'',
      repeatpassword:'',
    },
    validationSchema: Yup.object({
      username: Yup.string().max(15, 'Must be 15 characters or less').required('Required'),
      email: Yup.string().email('Invalid email address').required('Required'),
      password:Yup.string().min(6,"Password is too short - should be 6 chars minimum.").required('Password required'),
      repeatpassword:Yup.string().oneOf([Yup.ref('password'), null], 'Passwords must match').required('Required'),
    }),
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });

    return (

      <Popup
      trigger={<button className="button"> Open Modal </button>}
      modal 
      nested    
    >
      {close=>(
        <div id="id01" className="modal " >
          <span onClick={close} className="close" title="Close Modal">&times;</span>
          <form className="modal-content" onSubmit={formik.handleSubmit}>
            <div className="container">
              <h1>Sign Up</h1>
              <p>Please fill in this form to create an account.</p>
              <hr />
              <label htmlFor="username"><b>Username</b></label>
              <input type="text" placeholder="Enter Username" name="username" required />

              <label htmlFor="email"><b>Email</b></label>
              <input type="text" placeholder="Enter Email" name="email" required />
        
              <label htmlFor="psw"><b>Password</b></label>
              <input type="password" placeholder="Enter Password" name="psw" required />
        
              <label htmlFor="psw-repeat"><b>Repeat Password</b></label>
              <input type="password" placeholder="Repeat Password" name="psw-repeat" required />
              
              <label>
                <input type="checkbox" readOnly="readOnly" name="remember"  /> Remember me
              </label>
        
              <p>By creating an account you agree to our <a className="legal_agreement" href="#" >Terms & Privacy</a>.</p>
        
              <div className="clearfix">
                <button type="button" className="cancelbtn" onClick={close} >Cancel</button>
                <button type="submit" className="signupbtn">Sign Up</button>
              </div>
            </div>
        </form>
      </div>)}
  </Popup>
  )
}

export default Signup
