import React,{useContext,useState} from 'react';
import {useDrag} from 'react-dnd';
import './Tile_container.css'
import {DndProvider,useDrop} from 'react-dnd'
import {HTML5Backend} from 'react-dnd-html5-backend'
import {UserContext} from '../../UserContext.js'
import {GamesContext} from '../GamesContext.js'
import {Tile} from '../Sachet.js'

const positions=[[2,1,"row","horizontal"],[1,2,"column","vertical"],[2,3,"row","horizontal"],[3,2,"column","vertical"]];

function Tile_container(value) {
  
    const {profile,setprofile}=useContext(UserContext)
    const {gameState,setgameState}=useContext(GamesContext)
    const container_name="tile_container "+positions[value.player_num][3]
    const containerstyle={
      gridColumn:positions[value.player_num][0],
      gridRow:positions[value.player_num][1],
    }
    const holderstyle={
      display:"flex",
      flexDirection:positions[value.player_num][2]
    }
/*
    const [{isOver},drop] = useDrop(()=>({
      accept: "active",
      drop: (tile)=> rearrangeTile(tile.id),
  }));  

  const rearrangeTile = (tile) =>{
      if (!tile_content){
          
          set_tile_content([tile])
      }
  }
  

  */

    
      return (
        
        <div className="tile_container" style={containerstyle}>
            <div>{gameState.Player_list[value.player_num].username}</div>   
            <div>{gameState.Player_list[value.player_num].score}</div> 
            <div className="tile_holder" style={holderstyle}>
                <DndProvider backend={HTML5Backend}>
                  {gameState.Player_list[value.player_num].tiles.map((tile)=>(
                    <Tile alphabet={tile[0]} score={tile[1]} />
                  ))}  
                </DndProvider>
            </div>
            <button type="button" className="challengebtn" visibility={gameState.Player_list[value.player_num].active}>Challenge</button> 
            <img src=".../UI_component/delete.png" className="delete_img" visibility={gameState.Player_list[value.player_num].challenge_active} />                
        </div>
      );
      
  }
    
export default Tile_container;
