import './Login.css';
import React, {useState} from 'react';
import {title,Titlerender} from "./Title_div.js"
import Signup from "./Signup.js"
import axios from 'axios';



function validatelogin(val){
  let errors={};
  if (!val.username){
    errors.username="Username or Email is required"
  }
  if (!val.password){
    errors.password="Password is required"
  }
  return errors
}

function App() {
  const [err, setErr] = useState({});
  const [values,setValues] =useState({
    username:'',
    password:''
  })
  const handleChange = e => {
    const { name, value } = e.target;
    console.log(name)
    console.log(value)
    console.log(values)
    setValues({
      ...values,
      [name]: value
    });
  };



  const handleSubmit = (e)=>{
    console.log(validatelogin(values))
    setErr(validatelogin(values))
    cons
    e.preventDefault();
    if(Object.keys(err).length==0){
      console.log("success!!!!!!!!!!!!!!!!!!!!!!!!!")
    }
  }

  return (
    <div className="main">
          <div className="main-section">
            <Titlerender tiles={title} />                 
              <div className="login-form">
                  <div className="log-content">
                      <form onSubmit={handleSubmit}>
                          <input type="text
                          " name="username" id="email" placeholder="E-mail address or username" onChange={handleChange} />
                          {err.email && <p>{err.email}</p>}
                          <input type="password" name="password" id="password" placeholder="Password" onChange={handleChange}/>
                          {err.password && <p>{err.password}</p>}
                          <input type="submit" value="Log In" />
                          <a href="https://www.google.com/">Forgotten password?</a>
                          <hr />
                          <Signup/> 
                          
                      </form>  
                  </div>
              </div>
            </div>
      </div>
  );
}

export default App;
