import './Login.css';
import React, {useState, useContext} from 'react';
import {title,Titlerender} from "./Title_div.js"
import Signup from "./Signup.js"
import axios from 'axios';
import GoogleLogin from 'react-google-login';
import {useHistory} from 'react-router-dom';
import {UserContext} from '../UserContext.js'

function Forgotpassword(){
  let schema =yup.object().shape({
    email:Yup.string().email('Invalid email address'),
  }) 

  const history=useHistory()
    function handleforgotpassword(data){
        let post_input={email:data}
        axios.post('http://localhost:5000/forgotpassword',post_input).then((res)=>{
        alert("An email with a reset token would be sent to your email if your email is already in our database.")
        history.push('/')})
    }

    return(
        <div className="main-section">
          <div className="login-form">
              <div className="log-content">
                  <form onSubmit={handleforgotpassword}> 
                    <div>Forgot Password </div>
                    <input type="text" name="forgotpassword" id="forgotpassword" placeholder="Email"  />
                    <input type="submit" value="Submit" />

                  </form>  
                  </div>
              </div>
            </div>
    )
}

export default Forgotpassword
