import './Gamespage.css';
import React, {useState,useContext,useEffect,useRef} from 'react';
import axios from 'axios';
import Popup from 'reactjs-popup';
import {UserContext} from '../UserContext.js';
import {Player} from './Player.js'
import {Link} from 'react-router-dom';
import io from 'socket.io-client';
import Joingame from './Main_game/Joingame.js'
import Action_bar from './Action/Actionbar.js'
import Alphabet_analytics from './Alphabet_analytics/Alphabet_analytics.js'
import Main_game from './Main_game.js' 


function Gamespage() {
    const {profile,setprofile}=useContext(UserContext)
    const socketRef =useRef()
    const [gameState,setgameState]=useState({
      gamestarted:false,
      turn_num:0,
      player_num:0,
      Player_list:[]
    })

    useEffect(()=>{
      socketRef.current=io.connect("http://localhost:5500")
      axios.get('http://localhost:5500/get_added_players',{params: {username:profile.username}}).then((res)=>{

        for (let i=0;i<res.data.length;i++){
          let temp_player=new Player(res.data[i].username)
          temp_player.draw_by_ids(res[i].tiles)
          setgameState({...gameState,
            ["Player_list"]: [...gameState.Player_list,temp_player]})
        }

        const your_player=new Player(profile.username)
        your_player.draw()

        axios.put("http://localhost:5500/add_player",{username:your_player.username,tiles:your_player.tiles})

        setgameState({...gameState,
          ["Player_list"]: [...gameState.Player_list,your_player]})
        socketRef.current.emit("emit_new_player",{username:your_player.username,tiles:your_player.tiles})
        
        socketRef.current.on("new_game", res=>{
          for (let i=0;i<res.data.length;i++){
          let temp_player=new Player(res.data[i].username)
          temp_player.draw_by_ids(res[i].tiles)
          setgameState({...gameState,
            ["Player_list"]: [...gameState.Player_list,temp_player]})}
        })

        }
      )
      
    },[])
    
    
      return (     
        <div className="Games_page">
            <Joingame/>
            <div className="Main_divider">
              <Alphabet_analytics />
              <Main_game />
              <Action_bar />
            </div>                               
        </div>
      );
  }
    
export default Gamespage;
