import React,{useContext,useState,useEffect,useRef} from 'react';
import {useDrag} from 'react-dnd';
import './Tile_container.css'
import {DndProvider,useDrop} from 'react-dnd'
import {HTML5Backend} from 'react-dnd-html5-backend'
import {UserContext} from '../../UserContext.js'
import {GamesContext,PlayerListContext,BlocksContext} from '../GamesContext.js'
import {Tile} from '../Sachet.js'

const positions=[[2,1,"row","horizontal"],[1,2,"column","vertical"],[2,3,"row","horizontal"],[3,2,"column","vertical"]];

function Tile_container(value) {
  
    const {profile,setprofile}=useContext(UserContext)
    const {gameState,setgameState}=useContext(GamesContext)
    const {playerlistState,setplayerlistState}=useContext(PlayerListContext)
    const {boardState,setboardState}=useContext(BlocksContext)
    const playerlistRef=useRef(null);
    const gamestateRef=useRef(null);
    const boardstateRef=useRef(null);
    playerlistRef.current=playerlistState
    gamestateRef.current=gameState
    boardstateRef.current=boardState

    const container_name="tile_container "+positions[value.player_num][3]
    const containerstyle={
      gridColumn:positions[value.player_num][0],
      gridRow:positions[value.player_num][1],
    }
    const holderstyle={
      display:"flex",
      flexDirection:positions[value.player_num][2]
    }
    useEffect(()=>{
      
      let your_index_challenge = (gamestateRef.current.your_index==playerlistState.length-1) ? 0 : gamestateRef.current.your_index+1
      let temp_playerlist=[...playerlistState]
      if(gamestateRef.current.player_turn==gamestateRef.current.your_index){
        temp_playerlist[gamestateRef.current.your_index].delete_active=true
        temp_playerlist[gamestateRef.current.your_index].tiles.forEach(tile=>{tile.active=true})
        setplayerlistState(temp_playerlist)
      }else{
        temp_playerlist[gamestateRef.currentyour_index].tiles.forEach(tile=>{tile.active=false})
        setplayerlistState(temp_playerlist)
      }
      if(gameState.player_turn!=your_index_challenge){  
        temp_playerlist[gameState.your_index].challenge_active=true
        setplayerlistState(temp_playerlist)
      }else{
        temp_playerlist[gameState.your_index].challenge_active=false
        setplayerlistState(temp_playerlist)
      }
    },[,gameState.player_turn])

    
    const [{isOver},drop] = useDrop(()=>({
      accept: "active",
      collect:monitor=>({
          isOver:!!monitor.isOver(),
      }),
      canDrop: playerlistRef.current[gamestateRef.current.your_index].active,
      drop: (tile)=> addDelete(tile),
    })); 
    function container_delete_state(drag_container,delete_tile){
      setplayerlistState(prevplayerlistState=>{
        let newplayerlistState=[...prevplayerlistState]
        newplayerlistState[gamestateRef.current.player_turn].tiles=drag_container
        newplayerlistState[gamestateRef.current.player_turn].delete_list.push(delete_tile)    
        return newplayerlistState
      })
    }

    function board_delete_state(board_index,drag_block,delete_tile){
      setboardState(prevboardState=>{
        let newboardState=[...prevboardState]
        newboardState.blocks[board_index]=drag_block
        return newboardState
      })
      setplayerlistState(prevplayerlistState=>{
        let newplayerlistState=[...prevplayerlistState] 
        newplayerlistState[gamestateRef.current.player_turn].delete_list.push(delete_tile) 
        return newplayerlistState
      })
    }

    function addDelete(drop_tile){
      let drag_block
      let drag_container
      let delete_tile
      if (drop_tile.board_index) {
        delete_tile=boardstateRef.current.blocks[drop_tile.board_index].content 
        delete_tile.container_index=undefined
        delete_tile.board_index=undefined
        drag_block=boardstateRef.current.blocks[drop_tile.board_index]
        drag_block.content=null
        board_delete_state(drop_tile.board_index,drag_block,delete_tile)
      }else{
        delete_tile=playerlistRef.current[gamestateRef.current.player_turn].tiles[drop_tile.container_index]
        drag_container=playerlistRef.current.tiles.filter(tile=>tile.id!=drop_tile.id);
        for (let i=0;i<drag_container.length;i++){drag_container[i].container_index=i}
        container_delete_state(drag_container,delete_tile)
      }
      gamestateRef.current.socket.emit("emit_delete_tile",{
        room_id:gamestateRef.current.room_id,
        player_turn:gamestateRef.current.player_turn,
        drag_board:(drop_tile.board_index)?drag_block:drop_tile.board_index,   
        drag_container:(drop_tile.container_index)?drag_container:drop_tile.container_index,
        tile:delete_tile,
      })
    }
    useEffect(()=>{
      gamestateRef.current.socket.current.on("delete_tile",(socket_out)=>{
        
        if(socket_out.drag_block){
          board_delete_state(socket_out.drag_container,socket_out.delete_tile)
        }else{
          container_delete_state(socket_out.board_index,socket_out.drag_block,socket_out.delete_tile)
        } 
    })
    },[])
    
    return (
      
      <div className={container_name} style={containerstyle}>
          <div className="name_display">{playerlistState[value.player_num].username}</div>   
          <div className="score_display">{playerlistState[value.player_num].score}</div> 
          <div className="tile_holder" style={holderstyle}>

              
                {playerlistState[value.player_num].tiles.map((tile,pos)=>(
                  <Tile tile={tile} orientation={positions[value.player_num][2]} key={pos}/>
                ))}  
              
          </div>
          <button type="button" ref={drop} className="challengebtn" visibility={playerlistState[value.player_num].challenge_active?"visible":"hidden"}>Challenge</button> 
          <img src=".../UI_component/delete.png" className="delete_img" visibility={playerlistState[value.player_num].delete_active?"visible":"hidden"} />                
      </div>
    );
    
  }
    
export default Tile_container;
