import sachet from './Sachet.js'

class Player{
    constructor(username){
      this.username=username;
      this.score=0;
      this.tiles=[];
      this.active=false
      this.delete_active=false;
      this.challenge_active=false;    
    }
  
    draw(){
        
        while(this.tiles.length<7 && !sachet.length==0){
            let index=sachet.length*Math.random()<<0
            let chosen_tile=sachet[index]
            sachet.splice(index,1)
            this.tiles.push(chosen_tile);        
        }   
    }

    draw_by_alphabet(alphabets){
        for (let alphabet in alphabets){   
            console.log("0000000000000") 
            let i=sachet.indexOf(alphabet)
            
            this.tiles.push(sachet[i])
            console.log("alphabet: ",alphabet,"i: ",i,"sachet: ",sachet[i])
            sachet.splice(i,1)    
            }      
        }
    

    remove(tile){
      if (Array.isArray(tile)){
  
          for (let i = 0; i< tile.length; i++){
              let index = this.tiles.indexOf(tile[i]);
              this.tiles.splice(index, 1)
          }
  
      }else{
  
      for (let i = 0; i< this.tiles.length; i++){
          if (tile.querySelectorAll("p")[0].innerHTML==this.tiles[i].querySelectorAll("p")[0].innerHTML){
              this.tiles.splice(i, 1)
              return
              }
          }
          throw "this tile is not in this list"
      }
      
      
  }
  }

  export {Player}
