import {UserContext} from '../../UserContext.js'
import Board from './Board.js'
import Tile_container from './Tile_container.js'
import Tile_container_other from './Tile_container_other.js'
import React, {useState,useContext,useEffect,useRef} from 'react';
import {GamesContext,Invisible_tile_context} from '../GamesContext.js'
import './Main_game.css'



function Main_game() {
  const [tile_visibility,set_tile_visibility]=useState("hidden")
    const {profile,setprofile}=useContext(UserContext)
    const {gameState,setgameState}=useContext(GamesContext)
      return (
        
        <div className="main_game">
            {gameState.Player_list.map((Player,pos)=>(
            (Player.username==profile.username)?
            <Tile_container player_num={pos}/>:
            <Tile_container_other player_num={pos}/>))}
            <Board/>                                           
        </div>
      );
  }
    
export default Main_game;
