import React, {useState,useContext,useEffect,useRef} from 'react';
import {UserContext} from '../../UserContext.js'
import {GamesContext,BlocksContext,PlayerListContext} from '../GamesContext.js'
import './Actionbar.css'

function Draw_button(){
    const {profile,setprofile}=useContext(UserContext)
    const {gameState,setgameState}=useContext(GamesContext)
    const {boardState,setboardState}=useContext(BlocksContext)
    const {playerlistState,setplayerlistState}=useContext(PlayerListContext)
    const [drawActive,setdrawActive]=useState(false) 

    function Draw(){
        setgameState((prevgameState=>{
            let newgameState={...prevgameState}
            newgameState.Player_list[prevgameState.your_index].draw()
        
            if (newgameState.Sachet.length==0){
            newgameState.endgame=true
            return newgameState
            }else{
            newgameState.Sachet=newgameState.Player_list[prevgameState.your_index].Sachet.concat(newgameState.Player_list[prevgameState.your_index].delete_list)
            newgameState.just_added=[]
            newgameState.turn_num+=1
            return newgameState
            }
        }))    
      }

    useEffect(()=>{
        let active_player_num=gameState.turn_num%gameState.player_num  
        let nothing_placed=!boardState.blocks.findIndex(block=>{
          if(block.content){
            if(block.content.just_added){
              return true
            }
          }
          return false
        })
        if(active_player_num==gameState.your_index && !gameState.Player_list[gameState.your_index].delete_list && nothing_placed){
          setdrawActive(true)
        }else{
          setdrawActive(false)
        }
      },[gameState.turn_num,boardState])

    return(
        <div>
            <button type="button" className="draw_button" onClick={Draw} disabled={drawActive}>Draw</button> 
        </div>)
}

export default Draw_button
