import './Login.css';
import React, {useState} from 'react';
import {title,Titlerender} from "./Title_div.js"
import Signup from "./Signup.js"
import axios from 'axios';
import GoogleLogin from 'react-google-login';
import {useHistory} from 'react-router-dom'



function validatelogin(val){
  let err={};
  if (!val.username){
    err.username="Username or Email is required"
  }
  if (!val.password){
    err.password="Password is required"
  }
  return err
}

function App() {
  const [state, setstate] = useState();
  const [err, setErr] = useState({});
  const history=useHistory()
  const [values,setValues] =useState({
    username:'',
    password:''
  })
  const handleChange = e => {
    const { name, value } = e.target;
    
    setValues({
      ...values,
      [name]: value
    });
  };



  const handleSubmit = (e)=>{
    
    setErr(validatelogin(values))
    e.preventDefault();
    
    if(Object.keys(err).length==0){

      axios.get('http://localhost:5000/login',{params: {values}}).
      then(res => {
      (res.data)? history.push('/Mainpage'): setErr(err.mismatch= "Username or password do not match")
      
    })
    }
    
  }

  return (
    <div className="main">
          <div className="main-section">
            <Titlerender tiles={title} />                 
              <div className="login-form">
                  <div className="log-content">
                      <form onSubmit={handleSubmit}>

                          
                          <input type="text" name="username" id="email" placeholder="E-mail address or username" onChange={handleChange} />
                          {err.username && <div>{err.username}</div>}
                          <input type="password" name="password" id="password" placeholder="Password" onChange={handleChange}/>
                          {err.password && <div>{err.password}</div>}
                          {err.mismatch && <div>{err.mismatch}</div>}
                          <input type="submit" value="Log In" />
                          <GoogleLogin
                            className="Google_login"
                            clientId="AIzaSyAVGVNseBF0YXlNGfUFxL-AuvfdwXYav9A"
                            buttonText="Continue with Google"
                            onSuccess={setstate}
                            onFailure={setstate}
                            cookiePolicy={'single_host_origin'}
                          />
                          <a href="https://www.google.com/">Forgotten password?</a>
                          <hr />
                          <Signup/> 
                          
                      </form>  
                  </div>
              </div>
            </div>
      </div>
  );
}

export default App;
