import React, {useState,useContext,useEffect,useRef} from 'react';
import {UserContext} from '../../UserContext.js'
import {GamesContext,BlocksContext,PlayerListContext} from '../GamesContext.js'
import './Actionbar.css'

function Score_button(){
    const {profile,setprofile}=useContext(UserContext)
    const {gameState,setgameState}=useContext(GamesContext)
    const {boardState,setboardState}=useContext(BlocksContext)
    const {playerlistState,setplayerlistState}=useContext(PlayerListContext)
    const [scoreActive,setscoreActive]=useState(false)

    useEffect(()=>{
        let active_player_num=gameState.turn_num%gameState.player_num   
        if(active_player_num==gameState.your_index && !playerlistState[gameState.your_index].delete_list){
          setscoreActive(true)
        }else{
          setscoreActive(false)
        }
      },[gameState.turn_num,gameState.playerlistState[gameState.your_index].delete_list])
    
      function Score(){
        if(check_validity()){

            setboardState((prevboardState=>{
                let tempboardState={...prevboardState}
                gameState.just_added.forEach(index=>{
                    tempboardState.blocks[index].content.just_added=false
                    tempboardState.blocks[index].content.challenge=true
                    tempboardState.blocks[index].content.active=false,
                    tempboardState.blocks[index].content.canDrag=false,
                    tempboardState.blocks[index].content.canDrop=false,
                    tempboardState.blocks[index].content.index=null
                })
                return tempboardState
            }))

            setgameState((prevgameState=>({...prevgameState,
                //player will draw
                turn_num:prevgameState.turn_num+1, 
                just_added:[]     
            })))  
        }
      }
    return(
        <div>
            <button type="button" className="draw_button" onClick={Score} disabled={!scoreActive}>Score</button> 
        </div>)
}

export default {Score_button}
