import './Mainpage.css';
import React, {useState,useContext} from 'react';
import axios from 'axios';
import Popup from 'reactjs-popup';
import {UserContext} from '../UserContext.js'
import Rooms from './Rooms.js';
import {Link, useParams, useHistory} from 'react-router-dom';



function Mainpage() {
  const {profile,setprofile}=useContext(UserContext)
  const history=useHistory()
  function create_new_game(){
    axios.post('http://localhost:5500/new_game',{username:profile.username},{headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    }}).then((res)=>{
    
    history.push('/Gamespage/'+res.data) })
  }

  const nullprofile = ()=>{
    setprofile(null)
  }
    return (
      <div className="main">
            <div className="main-section">
                <div id="menu">
                  <div onClick={create_new_game}>New Game</div>
                  <div>Random Server</div>
                  <Rooms history={history}/>
                  <Link to="/" onClick={nullprofile}>Logout</Link>
              </div>
            </div>
        </div>
    );
}
  
  export default Mainpage;
