import {UserContext} from '../UserContext.js'
import io from 'socket.io-client'
import React, {useState,useContext,useEffect,useRef} from 'react';



function Joingame() {
    const {profile,setprofile}=useContext(UserContext)
    const [other_player,set_other_player] = useState([])
    const socketRef =useRef()

    useEffect(() => {
        socketRef.current=io.connect("http://localhost:5500")
        console.log("#######")
        socketRef.current.emit("emit_join_game",profile.username)
        socketRef.current.on("join_game", username=>{set_other_player(prev=>{prev})})
    },[])
    
    
    if(other_player){
      return (
        <div className="main">
              {other_player.map((Sentence)=><span>{Sentence}</span>)}              
        </div>
      )}else{return(null)}
  }

export default Joingame;
