import React, {useState,useContext,useEffect,useRef} from 'react';
import {UserContext} from '../../UserContext.js'
import {GamesContext,BlocksContext,PlayerListContext} from '../GamesContext.js'
import {games_sachet} from '../Gamespage.js'
import './Actionbar.css'

function Draw_button(values){
    const {profile,setprofile}=useContext(UserContext)
    const {playerlistState,setplayerlistState}=useContext(PlayerListContext)
    const {gameState,setgameState}=useContext(GamesContext)   
    const playerlistRef=useRef(null);
    const gamestateRef=useRef(null);
    playerlistRef.current=playerlistState
    gamestateRef.current=gameState
    const [drawActive,setdrawActive]=useState(false) 

    function Draw(){
      var drawn_tiles=playerlistRef.current[gamestateRef.current.player_turn].draw()
      let return_tiles=playerlistRef.current[gamestateRef.current.player_turn].delete_list
      for (let i=0;i<return_tiles.length;i++){
        return_tiles[i].canDrop=false
        return_tiles[i].canDrag=false
        return_tiles[i].container_index=null
        return_tiles[i].board_index=null
        return_tiles[i].placed=false
        return_tiles[i].just_added=false
      }
      return_tiles.forEach((return_tile)=>{games_sachet.push(return_tile)})
      console.log("SACHET",games_sachet)
      setplayerlistState((prevplayerlist=>{
        let newplayerlist=[...prevplayerlist]
        newplayerlist[gamestateRef.current.player_turn].tiles=newplayerlist[gamestateRef.current.player_turn].tiles.concat(drawn_tiles)
        newplayerlist[gamestateRef.current.player_turn].delete_list=[]
        return newplayerlist
      }))
      values.next_func()
      gamestateRef.current.socket.emit("emit_draw",{
        player_turn:gamestateRef.current.player_turn,
        drawn_tiles:drawn_tiles,
        removed_tiles:playerlistRef.current[gamestateRef.current.player_turn].delete_list
      })
      }
      useEffect(()=>{
        gamestateRef.current.socket.current.on("update_player_draw",(socket_out)=>{
          games_sachet=games_sachet.concat(socket_out.removed_tiles)
          setplayerlistState((prevplayerlist=>{
            let newplayerlist=[...prevplayerlist]
            newplayerlist[gamestateRef.player_turn].draw_by_tiles(socket_out.drawn_tiles)
            newplayerlist[gamestateRef.player_turn].delete_list=[]
            return newplayerlist
          }))
          values.next_func()
      })},[])

    useEffect(()=>{
        if(gamestateRef.current.player_turn==gamestateRef.current.your_index && !gamestateRef.current.just_added.length){
          setdrawActive(true)
        }else{
          setdrawActive(false)
        }
      },[,gamestateRef.current.player_turn,gamestateRef.current.just_added])

    return(
        <div>
            <button type="button" className="draw_button" onClick={Draw} disabled={!drawActive}>Draw</button> 
        </div>)
}

export default Draw_button
