import './Mainpage.css';
import React, {useState,useContext} from 'react';
import axios from 'axios';
import Popup from 'reactjs-popup';
import {UserContext} from '../UserContext.js'
import {Link, useParams} from 'react-router-dom';



function Mainpage() {
  const {profile,setprofile}=useContext(UserContext)

  function create_new_game(){
    axios.post("new_game",profile.username).then((res)=>{history.push('/Gamespage/'+res.data) })
  }

  const nullprofile = ()=>{
    setprofile(null)
  }
    return (
      <div className="main">
            <div className="main-section">
                <div id="menu">
                  <div onClick={create_new_game}>New Game</div>
                  <div>Random Server</div>
                  <div>Find Servers</div>
                  <Link to="/" onClick={nullprofile}>Logout</Link>
              </div>
            </div>
        </div>
    );
}
  
  export default Mainpage;
