import {GamesContext,BlocksContext,PlayerListContext} from '../GamesContext.js'
import './Actionbar.css'

function Score_button(){
    const {profile,setprofile}=useContext(UserContext)
    const {gameState,setgameState}=useContext(GamesContext)
    const {boardState,setboardState}=useContext(BlocksContext)
    const {playerlistState,setplayerlistState}=useContext(PlayerListContext)
    const [scoreActive,setscoreActive]=useState(false)
    console.log("List 2",playerlistState)
    useEffect(()=>{ 
        if(gameState.player_turn==gameState.your_index && !playerlistState[gameState.your_index].delete_list){
          setscoreActive(true)
        }else{
          setscoreActive(false)
        }
      },[gameState.player_turn,playerlistState[gameState.your_index].delete_list])
    
      function Score(){
        /*
